import React, { useCallback, useEffect, useState, useRef } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useSpring, animated } from "@react-spring/web";
import "./HomePage.css";

const HomePage = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { label: "ADHDR", path: "/" },
    { label: "About Us", path: "/about-us" },
    { label: "Your Results", path: "/your-results" },
    { label: "ADHD Resources", path: "/adhd-resources" },
    { label: "Contact Us", path: "/contact" },
  ];

  const handleNavigation = useCallback(
    (path) => {
      navigate(path);
    },
    [navigate]
  );

  const fadeProps = useSpring({
    opacity: 1,
    transform: "translateY(0px)",
    from: { opacity: 0, transform: "translateY(100px)" },
    config: { duration: 800 },
  });

  const [isTyping, setIsTyping] = useState(false);
  const [typedText, setTypedText] = useState("");
  const footerRef = useRef(null);

  const fullText =
    "ADHDR: integrating gamification and dynamic questionnaires to simplify the diagnosis process";

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsTyping(true);
        }
      },
      { threshold: 0.5 }
    );
    if (footerRef.current) {
      observer.observe(footerRef.current);
    }
    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    if (!isTyping) return;

    let index = 0;
    setTypedText("");
    const typingInterval = setInterval(() => {
      if (index < fullText.length) {
        setTypedText((prev) => prev + fullText.charAt(index));
        index++;
      } else {
        clearInterval(typingInterval);
      }
    }, 100);

    return () => clearInterval(typingInterval);
  }, [isTyping]);

  return (
    <div className="home-page">
      {/* Header */}
      <div className="header">
        {navItems.map((item) => (
          <div
            key={item.path}
            className={`nav-item ${
              location.pathname === item.path ? "nav-item-active" : ""
            }`}
            onClick={() => handleNavigation(item.path)}
          >
            {item.label}
          </div>
        ))}
      </div>

      {/* Main Content */}
      <div className="home">
        <animated.div style={fadeProps}>
          {/* Replace with your existing content */}
          <div className="sample-content">
            <h2>Welcome to ADHDR</h2>
            <p>Discover tools to simplify ADHD diagnosis through gamification.</p>
          </div>
        </animated.div>
      </div>

      {/* Footer */}
      <div className="navigation-footer1" ref={footerRef}>
        <p className="adhdr4">
          {typedText}
          {typedText.length < fullText.length && <span className="ellipsis">...</span>}
        </p>
      </div>
    </div>
  );
};

export default HomePage;
